import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import '../styles/style.css';

const Nav = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [animation, setAnimation] = useState('');

    const toggleOpen = () => {
        if (!isOpen) {
            setAnimation('');
            setIsOpen(true);
        } else {
            setAnimation('fadeOut');
        }
    };

    useEffect(() => {
        if (animation === 'fadeOut') {
            const timer = setTimeout(() => {
                setIsOpen(false); // Скрываем после анимации
                setAnimation(''); // Сброс анимации после выполнения
            }, 500); // Продолжительность анимации fadeOut
            return () => clearTimeout(timer);
        }
    }, [animation]);

    useEffect(() => {
        const handleClickOutside = (event) => {
            const navbarCollapse = document.getElementById('navbarNav');
            const navbarToggler = document.querySelector('.navbar-toggler');
            if (navbarCollapse && !navbarCollapse.contains(event.target) && navbarToggler && !navbarToggler.contains(event.target)) {
                if (isOpen && navbarCollapse.classList.contains('show')) {
                    setAnimation('fadeOut');
                }
            }
        };

        document.addEventListener('click', handleClickOutside);

        return () => {
            document.removeEventListener('click', handleClickOutside);
        };
    }, [isOpen]);

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container">
                <NavLink className="navbar-brand" to="/">
                    <img src="/photos/logo-fotor-2025031022326.png" alt="Logo" height="50" style={{ borderRadius: '20px' }} />
                </NavLink>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav ms-auto">
                        <li className="nav-item dropdown">
                            <NavLink className={({ isActive }) =>
                                isActive ? "nav-link dropdown-toggle active" : "nav-link dropdown-toggle"
                            } to="/services" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                                Услуги
                            </NavLink>
                            <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
                                <li>
                                    <NavLink className={({ isActive }) =>
                                        isActive ? "dropdown-item active" : "dropdown-item"
                                    } to="/key-making">
                                        Изготовление ключей
                                    </NavLink>
                                </li>
                                <li><NavLink className={({ isActive }) =>
                                    isActive ? "dropdown-item active" : "dropdown-item"
                                } to="/chip-programming">Запись Чипов</NavLink></li>
                                <li>
                                    <NavLink className={({ isActive }) =>
                                        isActive ? "dropdown-item active" : "dropdown-item"
                                    } to="/lock-repair">
                                        Восстановление Ключей и Чипов
                                        </NavLink>
                                </li>
                                <li> 
                                    <NavLink className={({ isActive}) => 
                                    isActive ? "dropdown-item active" : "dropdown-item"
                                } to="/">
                                    Компьютерная Диагностика
                                </NavLink>
                                </li>
                                <li>
                                    <NavLink className={({ isActive}) => 
                                    isActive ? "dropdown-item active" : "dropdown-item"
                                } to="/">
                                    Ремонт Иммобилайзера
                                </NavLink>
                                </li>
                                <li>
                                    <NavLink className={({ isActive}) => 
                                    isActive ? "dropdown-item active" : "dropdown-item"
                                } to="/">
                                    Ремонт Подушек безопасности
                                </NavLink>
                                </li>   
                                <li>
                                    <NavLink className={({ isActive}) => 
                                    isActive ? "dropdown-item active" : "dropdown-item"
                                } to="/">
                                    Вскрытие и Ремонт Автозамков
                                </NavLink>
                                </li>
                                <li>
                                    <NavLink className={({ isActive}) => 
                                    isActive ? "dropdown-item active" : "dropdown-item"
                                } to="/">
                                    Сейфы
                                </NavLink>
                                </li>
                                <li>
                                    <NavLink className={({ isActive}) => 
                                    isActive ? "dropdown-item active" : "dropdown-item"
                                } to="/">
                                    Дверные Замки
                                </NavLink>
                                </li>
                            </ul>
                        </li>
                        <li className="nav-item dropdown">
                            <NavLink className={({ isActive }) =>
                                isActive ? "nav-link dropdown-toggle active" : "nav-link dropdown-toggle"
                            } to="/shop" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                                Магазин
                            </NavLink>
                            <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
                                <li><NavLink className={({ isActive }) =>
                                    isActive ? "dropdown-item active" : "dropdown-item"
                                } to="/usa-products">Товары из Америки на заказ</NavLink></li>
                                <li><NavLink className={({ isActive }) =>
                                    isActive ? "dropdown-item active" : "dropdown-item"
                                } to="/key-blanks">Продажа Различных Заготовок ключей</NavLink></li>
                            </ul>
                        </li>
                        <li className="nav-item">
                            <NavLink
                                to="/contacts"
                                className={({ isActive }) =>
                                    isActive ? "nav-link active" : "nav-link"
                                }
                            >
                                Контакты
                            </NavLink>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    );
}

export default Nav;
